import { CommonModule } from '@angular/common';
import { HttpClientModule } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, ReactiveFormsModule, Validators } from '@angular/forms';
import { BrowserAnimationsModule } from '@angular/platform-browser/animations';
import { Router, RouterModule } from '@angular/router';
import { MdbAccordionModule } from 'mdb-angular-ui-kit/accordion';
import { MdbCarouselModule } from 'mdb-angular-ui-kit/carousel';
import { MdbCheckboxModule } from 'mdb-angular-ui-kit/checkbox';
import { MdbCollapseModule } from 'mdb-angular-ui-kit/collapse';
import { MdbDropdownModule } from 'mdb-angular-ui-kit/dropdown';
import { MdbFormsModule } from 'mdb-angular-ui-kit/forms';
import { MdbModalModule } from 'mdb-angular-ui-kit/modal';
import { MdbPopoverModule } from 'mdb-angular-ui-kit/popover';
import { MdbRadioModule } from 'mdb-angular-ui-kit/radio';
import { MdbRangeModule } from 'mdb-angular-ui-kit/range';
import { MdbRippleModule } from 'mdb-angular-ui-kit/ripple';
import { MdbScrollspyModule } from 'mdb-angular-ui-kit/scrollspy';
import { MdbTabsModule } from 'mdb-angular-ui-kit/tabs';
import { MdbTooltipModule } from 'mdb-angular-ui-kit/tooltip';
import { MdbValidationModule } from 'mdb-angular-ui-kit/validation';
import { UserContentModel } from '../services/interfaces/interface.service';
import { ServerApiService } from '../services/server/server-api.service';
import { UserService } from '../services/userMgmt/user.service';
import { Subscription } from 'rxjs';

@Component({
  selector: 'app-content-submission',
  standalone: true,
  imports: 
  [
    ReactiveFormsModule,
    CommonModule,
    MdbAccordionModule,
    MdbCarouselModule,
    MdbCheckboxModule,
    MdbCollapseModule,
    MdbDropdownModule,
    MdbFormsModule,
    MdbModalModule,
    MdbPopoverModule,
    MdbRadioModule,
    MdbRangeModule,
    MdbRippleModule,
    MdbScrollspyModule,
    MdbTabsModule,
    MdbTooltipModule,
    MdbValidationModule,
    ReactiveFormsModule,
    HttpClientModule,
    RouterModule
  ],
  templateUrl: './content-submission.component.html',
  styleUrl: './content-submission.component.scss'
})
export class ContentSubmissionComponent implements OnInit {

  contentForm : FormGroup
  showLoading : boolean = false ;
  regsisterationReponse : string = null;
  registerError : boolean = false ;
  showErrorMessage : boolean = false;
  private activeIDSubscription : Subscription
  private activeUserId = null ;



  constructor(
    private fb: FormBuilder,
    private router : Router,
    private server:ServerApiService,
    private Users : UserService
  )
  {
    this.activeIDSubscription = this.Users.activeUserIdObservble.subscribe(
      (e) => {this.activeUserId = e;}
    );
  }
  ngOnInit(): void {
    this.buildContentSubmissionForm();
  }

  private buildContentSubmissionForm()
  {
    this.contentForm = this.fb.group({
      title : ['', [Validators.required]],
      description : ['', [Validators.required]],
      link: ['', [Validators.required]],
      info: ['', ],
    });
  }



  async callSubmitContentApi()
  {
    console.log("here")
    this.showLoading = true ;
    let newContent : UserContentModel = 
    {
      UserId:this.activeUserId,
      Title : this.contentForm.controls.title.value,
      Description: this.contentForm.controls.description.value,
      Link: this.contentForm.controls.link.value,
      Info: this.contentForm.controls.info.value

    
    }
    ;

    try
    {
      await this.server.addUserContentFunction(newContent).then
      (
        (fulfilled:any) =>
        {
          this.showErrorMessage = true;
          this.regsisterationReponse = fulfilled.Message ;
          this.showLoading = false;
          this.registerError = false;
        },
        (rejected) =>
        {
          console.log(rejected,"rejected");
          this.showErrorMessage = true;
          this.regsisterationReponse = rejected.Message ;
          this.showLoading = false;
          this.registerError = true;

        }
      )
     
      
      setTimeout(() => {
        this.showErrorMessage = false ;
        this.router.navigateByUrl('/cc-dashboard')
      }, 3000);
    }
    catch(err)
    {
      this.showLoading = false;
      console.error(err) ;
    }
  

  }

}
<div class="FullDisplay" style="width: 100%;height: 100vh;display: flex;justify-content: center; 
align-items: center;align-content: center;justify-items: center;">
    <div class="displayForm" style="width: 70%;height: 70%;" >

        <form [formGroup]="contentForm" (ngSubmit)="callSubmitContentApi()">
            <!-- 2 column grid layout with text inputs for the first and last names -->
            <div class="row mb-4">
              <div class="col">
                <div data-mdb-input-init class="form-outline">
                  <input mdbValidate type="text" id="form6Example1" class="form-control" formControlName="title" />
                  <label class="form-label" for="form6Example1">Title</label>
                </div>
              </div>
              <div class="col">
                <div data-mdb-input-init class="form-outline">
                  <input mdbValidate type="text" id="form6Example2" class="form-control" formControlName="description" />
                  <label class="form-label" for="form6Example2">Description</label>
                </div>
              </div>
            </div>

            <div data-mdb-input-init class="form-outline mb-4">
                <input mdbValidate type="text" id="form6Example3" class="form-control" formControlName="link" />
                <label class="form-label" for="form6Example3">Link</label>
              </div>
          

            <div data-mdb-input-init class="form-outline mb-4">
              <textarea class="form-control" id="form6Example7" rows="4" formControlName="info"></textarea>
              <label class="form-label" for="form6Example7">Additional information</label>
            </div>
          
      
          
            <button data-mdb-ripple-init type="button" [disabled]="!contentForm.valid"
            *ngIf="showLoading==false"
                   type="submit" class="btn btn-primary btn-block mb-4"
                class="btn btn-primary btn-block mb-4">Create Content</button>
                <div class="row" style="display: flex; 
                justify-content: center;" *ngIf="showLoading==true">
                  <div class="spinner-border text-primary" role="status" style="display: flex;justify-content: center;">
                    <span class="visually-hidden">Loading...</span>
                  </div>
                </div>
          </form>
    
    </div>
</div>
