import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, ReactiveFormsModule } from '@angular/forms';

@Component({
  selector: 'app-content-submission',
  standalone: true,
  imports: 
  [
    ReactiveFormsModule
  ],
  templateUrl: './content-submission.component.html',
  styleUrl: './content-submission.component.scss'
})
export class ContentSubmissionComponent implements OnInit {
  contentForm : FormGroup

  constructor(
    private fb: FormBuilder,
  )
  {

  }
  ngOnInit(): void {
    this.buildContentSubmissionForm();
  }

  private buildContentSubmissionForm()
  {

  }



  callSubmitContentApi()
  {

  }

}
<div class="FullDisplay" style="width: 100%;height: 100%;display: flex;justify-content: center;
align-items: center;align-content: center;justify-items: center;">
    <div class="displayForm" style="width: 70%;height: 70%;" >

        <form [formGroup]="contentForm" (ngSubmit)="callSubmitContentApi()">
            <!-- 2 column grid layout with text inputs for the first and last names -->
            <div class="row mb-4">
              <div class="col">
                <div data-mdb-input-init class="form-outline">
                  <input type="text" id="form6Example1" class="form-control" />
                  <label class="form-label" for="form6Example1">Title</label>
                </div>
              </div>
              <div class="col">
                <div data-mdb-input-init class="form-outline">
                  <input type="text" id="form6Example2" class="form-control" />
                  <label class="form-label" for="form6Example2">Description</label>
                </div>
              </div>
            </div>
          

            <!-- Message input -->
            <div data-mdb-input-init class="form-outline mb-4">
              <textarea class="form-control" id="form6Example7" rows="4"></textarea>
              <label class="form-label" for="form6Example7">Additional information</label>
            </div>
          
      
          
            <!-- Submit button -->
            <button data-mdb-ripple-init type="button" class="btn btn-primary btn-block mb-4">Place order</button>
          </form>
    
    </div>
</div>
