import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { UserRegistrationModel } from '../interfaces/interface.service';

@Injectable({
  providedIn: 'root'
})


export class ServerApiService {

  constructor(
    private http : HttpClient
  ) { }
  private  BaseUrl =  "http://localhost:3000/" ;

  url_Create_New_CC = this.BaseUrl + 
                      'api/addNewCC' ;

  async addNewEvalloCCFunc(NewUser :UserRegistrationModel )
  {
    let response  ;

    return await new Promise(

      (resolve,reject) =>
      {
        
      }
    )

    try
    {
      this.http.post(this.url_Create_New_CC,NewUser).subscribe(
        {
          next: (value) => {
            console.log("next",value) ;
            response = {Error:null,Message: "Registered Successfully,Redirecting"} ;
          },
          error: (error) => {
            console.error("error",error) ;
            response = {Error:true,Message: "User Not Registered"} ;
          },
          complete: () => {
            console.error("complete") ;
          }
        }
        
      )
    }
    catch(err)
    {
      console.error(err);
      response={Error:true,Message: "User Not Registered"};
    }


    
    return response ;
  }

  




}
