import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { UserRegistrationModel } from './services/interfaces/interface.service';
import { ServerApiService } from './services/server/server-api.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})


export class AppComponent  implements OnInit {

  title = 'Welcome to Evallo';
  ccRegisForm : FormGroup ;
  showLoading : boolean = false ;
  showErrorMessage : boolean = false;
  regsisterationReponse : string = null;
  registerError : boolean = false ;

  loginForm : FormGroup;


  constructor(
    private fb: FormBuilder,
    private server:ServerApiService,
    private router : Router
    )
  {

  }

  ngOnInit(): void {
    this.buildCCRegisForm();
    this.buildLoginForm();
  }





  private buildCCRegisForm()
  {
    this.ccRegisForm = this.fb.group({
      registerName : ['', [Validators.required]],
      registerUsername : ['', [Validators.required]],
      registerEmail: ['', [Validators.required, Validators.email]],
      registerPassword: ['', [Validators.required]],
    });
  }
  private buildLoginForm()
  {
    this.loginForm = this.fb.group({
      loginCred : ['', [Validators.required]],
      loginPass : ['', [Validators.required]],
    });
  }

  async addNewCCForEvallo()
  {
    this.showLoading = true ;
    let newUser : UserRegistrationModel = 
    {
      Name : this.ccRegisForm.controls.registerName.value,
      username: this.ccRegisForm.controls.registerUsername.value,
      userEmail: this.ccRegisForm.controls.registerEmail.value,
      userPassword: this.ccRegisForm.controls.registerPassword.value
    }
    ;

    try
    {
      await this.server.addNewEvalloCCFunc(newUser).then
      (
        (fulfilled:any) =>
        {
          this.showErrorMessage = true;
          this.regsisterationReponse = fulfilled.Message ;
          this.showLoading = false;
          this.registerError = false;
        },
        (rejected) =>
        {
          console.log(rejected,"rejected");
          this.showErrorMessage = true;
          this.regsisterationReponse = rejected.Message ;
          this.showLoading = false;
          this.registerError = true;

        }
      )


      setTimeout(() => {
        this.showErrorMessage = false ;
        this.router.navigateByUrl('/cc-dashboard')
      }, 3000);
    }
    catch(err)
    {
      this.showLoading = false;
      console.error(err) ;
    }



    console.log(this.ccRegisForm.value);



  }

  async callLoginApi()
  {

    let userCreds = 
    {
      Name : this.ccRegisForm.controls.registerName.value,
      username: this.ccRegisForm.controls.registerUsername.value,
      userEmail: this.ccRegisForm.controls.registerEmail.value,
      userPassword: this.ccRegisForm.controls.registerPassword.value
    }
    ;
    callLoginApi
  }



}
<div class="container pt-5">
  <div class="d-flex justify-content-center align-items-center pt-5">
    <div class="">
      <div class="text-center">
        <img
          class="mb-4"
          src="https://mdbootstrap.com/img/logo/mdb-transparent-250px.png"
          style="width: 180px"
        />
      </div>
      <h5 class="mb-3" style="text-align: center;">Welcome to Evallo</h5>



      <hr />


        <div class="pillSwitcher">

          <mdb-tabs [pills]="true" [fill]="true">
            <mdb-tab title="Login">
              <form [formGroup]="loginForm" (ngSubmit)="callLoginApi()">

                <mdb-form-control class="mb-4">
                  <input mdbValidate mdbInput type="email" id="loginName" class="form-control" formControlName="loginCred" />
                  <label mdbValidate mdbLabel class="form-label" for="loginName">Email or username</label>
                </mdb-form-control>

                <mdb-form-control class="mb-4">
                  <input mdbInput type="password" id="loginPassword" class="form-control" formControlName="loginPass"/>
                  <label mdbLabel class="form-label" for="loginPassword">Password</label>
                </mdb-form-control>


                <button mdbRipple [disabled]="!loginForm.valid"
                 type="submit" class="btn btn-primary btn-block mb-4">
                  Sign in
                </button>


              </form></mdb-tab
            >
            <mdb-tab title="Content Creator Registration">
              <form [formGroup]="ccRegisForm" (ngSubmit)="addNewCCForEvallo()">

                <mdb-form-control class="mb-4">
                  <input mdbValidate mdbInput type="text" id="registerName" class="form-control" formControlName="registerName" />
                  <label mdbLabel class="form-label" for="registerName">Name</label>
                </mdb-form-control>

                <mdb-form-control class="mb-4">
                  <input mdbValidate mdbInput type="text" id="registerUsername" class="form-control" formControlName="registerUsername" />
                  <label mdbLabel class="form-label" for="registerUsername">Username</label>
                </mdb-form-control>

                <mdb-form-control class="mb-4">
                  <input mdbValidate mdbInput type="email" id="registerEmail" class="form-control" formControlName="registerEmail" />
                  <label mdbLabel class="form-label" for="registerEmail">Email</label>
                </mdb-form-control>

                <mdb-form-control class="mb-4" >
                  <input mdbValidate mdbInput type="password" id="registerPassword" class="form-control" formControlName="registerPassword" />
                  <label mdbLabel class="form-label" for="registerPassword">Password</label>
                </mdb-form-control>



                <button mdbRipple type="submit" [disabled]="!ccRegisForm.valid" *ngIf="showLoading==false"
                  class="btn btn-primary btn-block mb-3">
                  Sign Up
                </button>
                <div class="row" style="display: flex; 
                justify-content: center;" *ngIf="showLoading==true">
                  <div class="spinner-border text-primary" role="status" style="display: flex;justify-content: center;">
                    <span class="visually-hidden">Loading...</span>
                  </div>
                </div>
                <div class="row" style="display: flex; 
                justify-content: center;text-align: center;" *ngIf="showErrorMessage==true" >

                    <span class="{{registerError?'text-danger':'text-success'}}">{{regsisterationReponse}}</span>

                </div>


              </form></mdb-tab
            >
          </mdb-tabs>


        </div>






    </div>
  </div>
</div>
